type Address = `0x${string}`;
type Chain = {
    /** ID in number form */
    id: bigint;
    /** Human-readable name */
    name: string;
    /** Internal network name */
    network: string;
    /** Currency used by the chain */
    nativeCurrency: NativeCurrency;
    /** Collection of RPC endpoints */
    rpcUrls: {
        [key: string]: RpcUrls;
        default: RpcUrls;
        public: RpcUrls;
    };
    paymasterRpcUrls: {
        [key: string]: RpcUrls;
    };
    /** Flag for testnet networks */
    testnet?: boolean;
    /** Explorer links*/
    explorers?: {
        [key: string]: readonly string[];
    };
};
type NativeCurrency = {
    /** Token address */
    address: Address;
    /** Human-readable name */
    name: string;
    /** Currency symbol */
    symbol: string;
    /** Number of decimals */
    decimals: number;
};
type RpcUrls = {
    http: readonly string[];
    websocket?: readonly string[];
};

